# 7 8 9

def calculatePos(pos):
    if pos[0] < 1:
        pos[0] = 1
    if pos[0] > 3:
        pos[0] = 3
    if pos[1] < 1:
        pos[1] = 1
    if pos[1] > 3:
        pos[1] = 3
    return pos

def calculatePosPartTwo(pos, move):
    changePos = pos[:]
    if move == 'U':
        changePos[1] = changePos[1] + 1
    elif move == 'D':
        changePos[1] = changePos[1] - 1
    elif move == 'L':
        changePos[0] = changePos[0] - 1
    elif move == 'R':
        changePos[0] = changePos[0] + 1
    if (abs(changePos[0])+abs(changePos[1])) > 2:
        changePos = pos[:]
    return changePos
        
def moveKeypad(pos, seq):
    for i in range(len(seq)):
        if seq[i] == 'U':
            pos[1] = pos[1] - 1
        elif seq[i] == 'D':
            pos[1] = pos[1] + 1
        elif seq[i] == 'L':
            pos[0] = pos[0] - 1
        elif seq[i] == 'R':
            pos[0] = pos[0] + 1
        else:
            0
        pos = calculatePos(pos)
    theCode = (pos[0] + (pos[1]-1)*3)
    return theCode

def moveKeypadPartTwo(pos, seq):
    for i in range(len(seq)):
        pos = calculatePosPartTwo(pos, seq[i])
    return pos

