def decipherCode(pos, codeString):
    theCode = []
    cipherLines = codeString.split("\n")
    for seq in cipherLines:
        theCode.append(moveKeypad(pos, seq))
    return theCode

def decipherCodePartTwo(pos, codeString):
    theCode = []
    cipherLines = codeString.split("\n")
    for seq in cipherLines:
        # keypadPos = moveKeypadPartTwo(pos, seq)
        theCode.append(moveKeypadPartTwo(pos, seq))
        # theCode.append(translatePos(keypadPos))
    return theCode

